function ifForeignIPHide(){if(getCookie("foreignIP")==="Y"){const b2cSubForeignIP=document.getElementsByClassName("b2cSubForeignIP");for(const b2cSub of b2cSubForeignIP){b2cSub.style.display="none"}}}function commaChk(num){if(typeof num==="number"){num=String(num)}return num.replace(/\B(?=(\d{3})+(?!\d))/g,",")}